import React, { useRef, useEffect } from "react";
import { Formik, Form, Field, ErrorMessage } from "formik";
import { useDispatch, useSelector } from "react-redux";
import {
  updateUserRequset,
  getUsersRequest,
} from "../../../actions/usersCreators";
import AutoAwesomeMotionIcon from "@mui/icons-material/AutoAwesomeMotion";
import CloseIcon from "@mui/icons-material/Close";
import * as Yup from "yup";
import cx from "classnames";
import styles from "./UserFormEdit.module.scss";

const UserFormEdit = ({ setModalActive }) => {
  const { users, offset } = useSelector(({ users }) => users);
  const dispatch = useDispatch();
  console.log(users);

  useEffect(() => {
    dispatch(getUsersRequest({ limit: 4, offset }));
  }, []);
  const filePicker = useRef(null);

  const onSubmit = (values, formikBag) => {
    dispatch(updateUserRequset({ values: values }));
    console.log(dispatch(updateUserRequset({ values: values })));
    formikBag.resetForm();
  };

  const handlerPick = () => filePicker.current.click();

  const validationShemas = Yup.object({
    login: Yup.string().required("Must be required"),
    password: Yup.string().required("Must be required"),
    name: Yup.string().required("Must be required"),
  });

  return (
    <>
      {users.map((user) => (
        <div className={styles.modal_container}>
          <div className={styles.modal_wrapper}>
            <Formik
              initialValues={{
                login: "",
                name: "",
                password: "",
                avatar: "",
              }}
              validationSchema={validationShemas}
              onSubmit={onSubmit}
            >
              {(formikProps) => (
                <Form className={styles.form}>
                  <div className={styles.close_modal}>
                    {
                      <CloseIcon
                        className={styles.modal_close_item}
                        onClick={() => setModalActive(false)}
                      />
                    }
                  </div>
                  <label className={styles.login}>
                    <Field name="name">
                      {({ field, form, meta }) => {
                        const inputClasses = cx(styles.registr_input, {
                          [styles.invalid]: meta.error && meta.touched,
                        });
                        return (
                          <input
                            {...field}
                            className={inputClasses}
                            placeholder="name"
                          />
                        );
                      }}
                    </Field>
                    <ErrorMessage
                      name="name"
                      component="div"
                      className={styles.error}
                    />
                  </label>
                  <label className={styles.login}>
                    <Field name="login">
                      {({ field, form, meta }) => {
                        const inputClasses = cx(styles.registr_input, {
                          [styles.invalid]: meta.error && meta.touched,
                        });
                        return (
                          <input
                            {...field}
                            className={inputClasses}
                            placeholder="login"
                          />
                        );
                      }}
                    </Field>
                    <ErrorMessage
                      name="login"
                      component="div"
                      className={styles.error}
                    />
                  </label>
                  <label className={styles.password}>
                    <Field name="password">
                      {({ field, form, meta }) => {
                        const inputClasses = cx(styles.registr_input, {
                          [styles.invalid]: meta.error && meta.touched,
                        });
                        return (
                          <input
                            {...field}
                            className={inputClasses}
                            placeholder="password"
                            type="password"
                          />
                        );
                      }}
                    </Field>
                    <ErrorMessage
                      name="password"
                      component="div"
                      className={styles.error}
                    />
                  </label>

                  <button
                    onClick={handlerPick}
                    className={styles.file_picker}
                    type="button"
                  >
                    <AutoAwesomeMotionIcon />
                  </button>
                  <input
                    type="file"
                    name="avatar"
                    ref={filePicker}
                    className={styles.hidden}
                    onChange={(e) =>
                      formikProps.setFieldValue("avatar", e.target.files[0])
                    }
                  />
                  <input type="submit" className={styles.submit} value="Save" />
                </Form>
              )}
            </Formik>
          </div>
        </div>
      ))}
    </>
  );
};

export default UserFormEdit;
