import { put } from "redux-saga/effects";
import {
  createUserSuccess,
  createUserError,
  getUsersSuccess,
  getUsersError,
  getUserByIdError,
  getUserByIdSuccess,
} from "../actions/usersCreators";
import { createUser, getAllUsers, getUserById } from "../api";

export function* createUserSaga(action) {
  try {
    const {
      data: { data: user },
    } = yield createUser(action.payload.values);
    yield put(createUserSuccess(user));
  } catch (error) {
    yield put(createUserError(error));
  }
}
export function* getAllUsersSaga(action) {
  try {
    const {
      data: { data: users },
    } = yield getAllUsers(action.payload);
    yield put(getUsersSuccess(users));
  } catch (error) {
    yield put(getUsersError(error));
  }
}
export function* getUserByIdSaga(action) {
  try {
    const {
      data: { data: user },
    } = yield getUserById(action.payload.id);
    yield put(getUserByIdSaga())
  } catch (error) {
    yield put(getUserByIdError(error));
  }
}
