import React, { useRef } from "react";
import { Formik, Form, Field, ErrorMessage } from "formik";
import { useDispatch, useSelector } from "react-redux";
import { createUserRequest } from "../../actions/usersCreators";
import * as Yup from "yup";
import cx from "classnames";
import styles from "./UserForm.module.scss";
import gomer from "./homer_registr.jpg";

const UserForm = () => {
  const dispatch = useDispatch();

  const { isFetching } = useSelector(({ users }) => users);

  const filePicker = useRef(null);

  const onSubmit = (values, formikBag) => {
    dispatch(createUserRequest(values));
    formikBag.resetForm();
  };

  const handlerPick = () => filePicker.current.click();

  const validationShemas = Yup.object({
    login: Yup.string()
      .matches(/^[A-Z][a-z]{1,15}$/)
      .required("Must be required"),
    password: Yup.string()
      .matches(/^(?=.*[A-Za-z])(?=.*\d)[A-Za-z\d]{8,}$/)
      .required("Must be required"),
  });
  return (
    <>
      {!isFetching ? (
        <Formik
          initialValues={{
            login: "",
            password: "",
            avatar: "",
          }}
          validationSchema={validationShemas}
          onSubmit={onSubmit}
        >
          {(formikProps) => (
            <Form className={styles.form}>
              <label>
                <Field
                  name="login"
                  placeholder="login"
                  // className={styles.registr_input}
                >
                  {({ field, form, meta }) => {
                    const inputClasses = cx(styles.registr_input, {
                      [styles.invalid]: meta.error && meta.touched,
                    });
                    return <input {...field} className={inputClasses} />;
                  }}
                </Field>
                <ErrorMessage
                  name="login"
                  component="div"
                  className={styles.error}
                />
              </label>
              <label>
                <Field
                  name="password"
                  placeholder="password"
                  className={styles.registr_input}
                ></Field>
                <ErrorMessage
                  name="password"
                  component="div"
                  className={styles.error}
                />
              </label>

              <button
                onClick={handlerPick}
                className={styles.file_picker}
                type="button"
              >
                Chooze file..
              </button>
              <input
                type="file"
                name="avatar"
                ref={filePicker}
                className={styles.hidden}
                onChange={(e) =>
                  formikProps.setFieldValue("avatar", e.target.files[0])
                }
              />
              <input type="submit" className={styles.submit} value="Next" />
            </Form>
          )}
        </Formik>
      ) : (
        <div className={styles.finish_registr}>
          <img src={gomer} alt="gomer" className={styles.gomer} />
          <div className={styles.finish_text}>
            <h2 className={styles.heading_finish}>All good!</h2>
            <h3>Registration successful</h3>
          </div>
        </div>
      )}
    </>
  );
};

export default UserForm;
