import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import ModeEditIcon from "@mui/icons-material/ModeEdit";
import HighlightOffIcon from "@mui/icons-material/HighlightOff";
import { useParams } from "react-router-dom";
import { getUserByIdRequest } from "../../actions/usersCreators";
import {
  getTaskUserRequest,
  createTaskRequest,
  deleteUserTaskRequest,
} from "../../actions/taskCreators";
import DeleteIcon from "@mui/icons-material/Delete";

import { Formik, Form, Field } from "formik";
import styles from "./UserProfile.module.scss";
import CreateTask from "../TaskComponent/CreateTask";
import TaskForm from "../forms/TaskForm";
import TaskEditForm from "../forms/TaskEditForm/index";
import UserProfileCard from "./UserProfileCard/index";

const UserProfile = () => {
  const { selectedUser } = useSelector(({ users }) => users);
  const { selectedTask } = useSelector(({ tasks }) => tasks);

  const [showTasks, setShowTasks] = useState(true);
  const [isCreateTask, setIsCreateTask] = useState(false);
  const [isEditTask, setIsEditTask] = useState(true);
  const { userId } = useParams();
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getUserByIdRequest({ userId }));
    dispatch(getTaskUserRequest({ userId }));

    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  return (
    <div className={styles.wrapper_profile}>
      <CreateTask
        isCreateTask={isCreateTask}
        setIsCreateTask={setIsCreateTask}
      />
      {isEditTask
        ? isCreateTask && <TaskForm userId={userId} />
        : isCreateTask && <TaskEditForm selectedTask={selectedTask} />}

      {selectedUser && <UserProfileCard selectedUser={selectedUser} />}
      {!showTasks && (
        <article className={styles.task_container}>
          {selectedTask && (
            <ul>
              {selectedTask.map((task) => (
                <li key={task.id} className={styles.task_list_item}>
                  <p className={styles.content_text}>
                    {task.content}
                    <button
                      className={styles.delete_task}
                      onClick={() => {
                        dispatch(
                          deleteUserTaskRequest({ userId, taskId: task.id })
                        );
                      }}
                    >
                      <DeleteIcon />
                    </button>
                    {isEditTask ? (
                      <button
                        className={styles.delete_task}
                        onClick={() => {
                          setIsEditTask(false);
                          setIsCreateTask(true);
                          console.log(task.id);
                        }}
                      >
                        <ModeEditIcon />
                      </button>
                    ) : (
                      <button
                        className={styles.delete_task}
                        onClick={() => {
                          setIsEditTask(true);
                          console.log(task.id);
                        }}
                      >
                        <HighlightOffIcon />
                      </button>
                    )}
                  </p>
                  <p className={styles.date}>{task.deadline}</p>
                </li>
              ))}
            </ul>
          )}
        </article>
      )}
    </div>
  );
};

export default UserProfile;
