import React, { useState } from "react";
import cx from "classnames";
import RemoveCircleOutlineIcon from "@mui/icons-material/RemoveCircleOutline";
import ModeEditIcon from "@mui/icons-material/ModeEdit";

import defaultImage from "../../../assets/images/default.jpg";
import UserFormEdit from "../../forms/UserFormEdit";
import styles from "./UserProfileCard.module.scss";

const UserProfileCard = ({ selectedUser, showTasks, setShowTasks }) => {
  const [modalActive, setModalActive] = useState(false);

  const taskClasses = cx(styles.profile_card, {
    [styles.activeCreateTask]: isSecureContext === true,
  });
  return (
    <article className={taskClasses}>
      {selectedUser.avatar ? (
        <img
          src={`http://localhost:3001/images/${selectedUser.avatar}`}
          alt={selectedUser.name}
          className={styles.avatar}
        />
      ) : (
        <img
          src={defaultImage}
          className={styles.avatar}
          alt={selectedUser.name}
        />
      )}

      <div className={styles.info}>
        <p className={styles.fullname}>{selectedUser.name}</p>
        <p className={styles.login}>{selectedUser.login}</p>
      </div>
      <button
        className={styles.delete}
        style={{ transform: "translateX(22px)" }}
        onClick={() => setModalActive(true)}
      >
        <ModeEditIcon />
      </button>
      {showTasks ? (
        <button
          className={styles.delete}
          style={{ fontSize: "0px" }}
          onClick={() => setShowTasks(false)}
        >
          <RemoveCircleOutlineIcon />
        </button>
      ) : (
        <button
          className={styles.delete}
          style={{ fontSize: "35px" }}
          onClick={() => setShowTasks(true)}
        >
          +
        </button>
      )}

      {modalActive && (
        <UserFormEdit
          setModalActive={setModalActive}
          user={selectedUser}
          modalActive={modalActive}
        />
      )}
    </article>
  );
};

export default UserProfileCard;
