import { put } from "redux-saga/effects";
import { getTaskUser } from "../api";
import { getTaskUserSuccess, getTaskUserError } from "../actions/taskCreators";

export function* getTaskUserSaga(action) {
  try {
    const {
      data: { data: task },
    } = yield getTaskUser(action.pa);
    yield put(getTaskUserSuccess({ task }));
    console.log(task);
  } catch (error) {
    yield put(getTaskUserError({ error }));
  }
}
