    const {
      data: { data },
    } = yield deleteUserTask({
      userId: action.payload.userId,
      taskId: action.payload.taskId,
    });
    yield put(deleteUserTaskSuccess(data));
  } catch (error) {
    yield put(deleteUserTaskError({ error }));
  }
}

