import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { useParams } from "react-router-dom";
import { getUserByIdRequest } from "../../actions/usersCreators";
import UserFormEdit from "../forms/UserFormEdit";

const UserProfile = () => {
  const { selectedUser } = useSelector(({ users }) => users);
  const [modalActive, setModalActive] = useState(true);

  const { userId } = useParams();
  console.log(selectedUser);
  console.log(userId);
  const dispatch = useDispatch();
  console.log(getUserByIdRequest({ userId }));

  useEffect(() => {
    dispatch(getUserByIdRequest({ userId }));
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);
  return (
    <div>
      {selectedUser && (
        <article>
          <p>{`${selectedUser.login} ${selectedUser.name}`}</p>
         setModalActive(false)}
        </article>
      )}
      {modalActive && <UserFormEdit />}
    </div>
  );
};

export default UserProfile;
