import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import {
  getUsersRequest,
  deleteUserByIdRequest,
} from "../../actions/usersCreators";
import { useNavigate } from "react-router-dom";
import Skeleton from "../Sceleton";
import styles from "./UserList.module.scss";

const UsersList = () => {
  const { users, isFetching, pageSize, totalUsersCount, currentPage } =
    useSelector(({ users }) => users);
  console.log(currentPage);
  const [search, setSearch] = useState("");

  const dispatch = useDispatch();

  const loadUsers = ({ page = currentPage, limit = pageSize, offset }) =>
    dispatch(getUsersRequest({ page, limit, offset }));

  useEffect(() => {
    loadUsers();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);
  console.log(users);
  const onChangeSearchValue = ({ target: { value } }) => {
    setSearch(value);
  };

  const navigate = useNavigate();

  let pagesCount = Math.ceil(totalUsersCount / pageSize);
  const pages = [];
  for (let i = 1; i <= pagesCount; i++) {
    pages.push(i);
  }
  return (
    <>
      <div className={styles.card_container}>
        <label className={styles.search_inner}>
          <svg
            viewBox="0 0 20 20"
            xmlns="http://www.w3.org/2000/svg"
            style={{ width: "20px" }}
            className={styles.lupa}
          >
            <path d="M12.9 14.32a8 8 0 1 1 1.41-1.41l5.35 5.33-1.42 1.42-5.33-5.34zM8 14A6 6 0 1 0 8 2a6 6 0 0 0 0 12z" />
          </svg>
          <input
            className={styles.search_user}
            value={search}
            onChange={onChangeSearchValue}
          />
        </label>
        {isFetching ? (
          <div className={styles.skeleton}>
            <Skeleton />
            <Skeleton />
            <Skeleton />
          </div>
        ) : (
          users
            .filter((u) => {
              const userName = u.name.toLowerCase();
              return (
                userName.toLowerCase().includes(search) ||
                u.login.toLowerCase().includes(search)
              );
            })
            .map((u) => (
              <article key={u.id} className={styles.card}>
                <img
                  src={`http://localhost:3001/images/${u.avatar}`}
                  className={styles.avatar}
                  alt={u.id}
                />
                <div className={styles.info}>
                  <p className={styles.fullname}>{u.name}</p>

                  <p
                    className={styles.name}
                    onClick={() => {
                      navigate(`/users/${u.id}`);
                    }}
                  >
                    {u.login}
                  </p>
                </div>
                <button
                  className={styles.delete}
                  onClick={() => dispatch(deleteUserByIdRequest(u.id))}
                >
                  x
                </button>
              </article>
            ))
        )}
        <div className={styles.navigate}>
          {pages.map((p, i) => (
            <span
              key={i}
              className={currentPage === p ? styles.selectedPage : currentPage}
            >
              {p}
            </span>
          ))}
        </div>
      </div>
    </>
  );
};

export default UsersList;
