import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { getUsersRequest } from "../../actions/usersCreators";
import styles from "./UserList.module.scss";
import { Link } from "react-router-dom";

const UsersList = () => {
  const { users } = useSelector(({ users }) => users);
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(getUsersRequest());
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);
  console.log(users);
  return (
    <>
      <ul className={styles.card_container}>
        <label>
          <svg viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg">
            <path d="M12.9 14.32a8 8 0 1 1 1.41-1.41l5.35 5.33-1.42 1.42-5.33-5.34zM8 14A6 6 0 1 0 8 2a6 6 0 0 0 0 12z" />
          </svg>
          <input className={styles.search_user} />
        </label>
        {users.map((u) => (
          <article key={u.id} className={styles.card}>
            <img
              src={`http://localhost:3000/images/${u.avatar}`}
              className={styles.avatar}
              alt={u.id}
            />
            <div className={styles.info}>
              <p className={styles.fullname}>{u.name}</p>
              <Link to={`/users/${u.id}`} className={styles.link_name}>
                <p className={styles.name}>{u.login}</p>
              </Link>
            </div>
          </article>
        ))}
      </ul>
    </>
  );
};

export default UsersList;
