import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import ModeEditIcon from "@mui/icons-material/ModeEdit";
import RemoveCircleOutlineIcon from "@mui/icons-material/RemoveCircleOutline";

import { useParams } from "react-router-dom";
import { getUserByIdRequest } from "../../actions/usersCreators";
import cx from "classnames";
import {
  getTaskUserRequest,
  createTaskRequest,
} from "../../actions/taskCreators";
import UserFormEdit from "../forms/UserFormEdit";
import DeleteIcon from "@mui/icons-material/Delete";

import { Formik, Form, Field } from "formik";
import defaultImage from "../../assets/images/default.jpg";
import styles from "./UserProfile.module.scss";

const UserProfile = () => {
  const { selectedUser } = useSelector(({ users }) => users);
  const { selectedTask, isFetching } = useSelector(({ tasks }) => tasks);
  console.log(isFetching);
  const taskClasses = cx(styles.profile_card, {
    [styles.activeCreateTask]: isSecureContext === true,
  });
  const [modalActive, setModalActive] = useState(false);
  const [showTasks, setShowTasks] = useState(true);
  const [isCreateTask, setIsCreateTask] = useState(false);
  const [isEditTask, setIsEditTask] = useState(true);
  const { userId } = useParams();
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getUserByIdRequest({ userId }));
    dispatch(getTaskUserRequest({ userId }));

    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);
  const onSubmit = (values, formikBag) => {
    dispatch(createTaskRequest({ values, userId }));
    formikBag.resetForm();
  };

  return (
    <div className={styles.wrapper_profile}>
      <div className={styles.title_wrapper}>
        <h2 className={styles.title}>Plan </h2>
        <span>your day</span>
        {isCreateTask ? (
          <button
            className={styles.onTask}
            onClick={() => setIsCreateTask(false)}
          >
            create your task
          </button>
        ) : (
          <button
            className={styles.onTask}
            onClick={() => setIsCreateTask(true)}
          >
            create your task
          </button>
        )}
      </div>
      {isEditTask
        ? isCreateTask && (
            <Formik
              initialValues={{
                content: "",
              }}
              onSubmit={onSubmit}
            >
              <Form className={styles.form_task}>
                <Field
                  name="content"
                  className={styles.content}
                  component="textarea"
                  placeholder="write your task..."
                />

                <input
                  type="submit"
                  value="send"
                  className={styles.add_task_button}
                />
              </Form>
            </Formik>
          )
        : isCreateTask && (
            <Formik
              initialValues={{
                content: selectedTask.content,
              }}
              onSubmit={onSubmit}
            >
              <Form className={styles.form_task}>
                <Field
                  name="content"
                  className={styles.content}
                  component="textarea"
                  placeholder="write your task..."
                />

                <input
                  type="submit"
                  value="save"
                  className={styles.add_task_button}
                />
              </Form>
            </Formik>
          )}

      {selectedUser && (
        <article className={taskClasses}>
          {selectedUser.avatar ? (
            <img
              src={`http://localhost:3001/images/${selectedUser.avatar}`}
              alt={selectedUser.name}
              className={styles.avatar}
            />
          ) : (
            <img
              src={defaultImage}
              className={styles.avatar}
              alt={selectedUser.name}
            />
          )}

          <div className={styles.info}>
            <p className={styles.fullname}>{selectedUser.name}</p>
            <p className={styles.login}>{selectedUser.login}</p>
          </div>
          <button
            className={styles.delete}
            style={{ transform: "translateX(22px)" }}
            onClick={() => setModalActive(true)}
          >
            <ModeEditIcon />
          </button>
          {!showTasks ? (
            <button
              className={styles.delete}
              style={{ fontSize: "0px" }}
              onClick={() => setShowTasks(true)}
            >
              <RemoveCircleOutlineIcon />
            </button>
          ) : (
            <button
              className={styles.delete}
              style={{ fontSize: "35px" }}
              onClick={() => setShowTasks(false)}
            >
              +
            </button>
          )}

          {modalActive && (
            <UserFormEdit
              setModalActive={setModalActive}
              user={selectedUser}
              modalActive={modalActive}
            />
          )}
        </article>
      )}
      {!showTasks && (
        <article className={styles.task_container}>
          {selectedTask && (
            <ul>
              {selectedTask.map((task) => (
                <li key={task.id} className={styles.task_list_item}>
                  <p className={styles.content_text}>
                    {task.content}
                    <button className={styles.delete_task}>
                      <DeleteIcon />
                    </button>
                    <button
                      className={styles.delete_task}
                      onClick={() => {
                        setIsEditTask(false);
                        setIsCreateTask(true);
                      }}
                    >
                      <ModeEditIcon />
                    </button>
                  </p>
                  <p className={styles.date}>{task.deadline}</p>
                </li>
              ))}
            </ul>
          )}
        </article>
      )}
    </div>
  );
};

export default UserProfile;
