import React, { useState } from "react";
import { useDispatch } from "react-redux";
import {
  deleteUserTaskRequest,
  getTaskUserRequest,
  updateUserTaskRequest,
} from "../../../actions/taskCreators";
import DeleteIcon from "@mui/icons-material/Delete";
import ModeEditIcon from "@mui/icons-material/ModeEdit";
import { format } from "date-fns";
import RemoveCircleOutlineIcon from "@mui/icons-material/RemoveCircleOutline";

import styles from "../UserProfile.module.scss";
import TaskEditForm from "../../forms/TaskEditForm";

const UserTaskList = ({ selectedTask, userId }) => {
  console.log(selectedTask);
  const dispatch = useDispatch();
  const [modalTask, setModalTask] = useState(false);
  const [editedTask, setEditedTask] = useState({});

  return (
    <ul>
      {selectedTask.map((task) => (
        <li key={task.id} className={styles.task_list_item}>
          <p className={styles.content_text}>
            {task.content}
            <button
              className={styles.delete_task}
              onClick={() => {
                dispatch(deleteUserTaskRequest({ userId, taskId: task.id }));
                dispatch(getTaskUserRequest({ userId }));
              }}
            >
              <DeleteIcon />
            </button>

            <button
              className={styles.delete_task}
              onClick={() => {
                setEditedTask(task);
              }}
            >
              <ModeEditIcon />
            </button>
          </p>
          <p className={styles.date}>
            {format(new Date(`${task.deadline}`), "PPPP")}
          </p>
        </li>
      ))}
      {modalTask && <TaskEditForm userId={userId} editedTask={editedTask} />}
    </ul>
  );
};

export default UserTaskList;
