import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";

import {
  getUsersRequest,
  setOffset,
  setCurrentPage,
} from "../../actions/usersCreators";

import Skeleton from "../Sceleton";
import UserItem from "../UserItem";
import styles from "./UserList.module.scss";

const UsersList = () => {
  const { users, isFetching, offset, currentPage } = useSelector(
    ({ users }) => users
  );

  const [search, setSearch] = useState("");
  const onChangeSearchValue = ({ target: { value } }) => {
    setSearch(value);
  };

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getUsersRequest({ limit: 4, offset }));
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [offset]);
  console.log(users);
  console.log(offset);

  const handlerNext = () => {
    dispatch(setOffset({ offset: offset + 4 }));
    dispatch(setCurrentPage(currentPage + 1));
  };

  const handlerBack = () => {
    dispatch(setOffset({ offset: offset - 4 }));
    dispatch(setCurrentPage(currentPage - 1));
  };
  return (
    <>
      <div className={styles.card_container}>
        {isFetching ? (
          <div className={styles.skeleton}>
            <Skeleton />
            <Skeleton />
            <Skeleton />
          </div>
        ) : (
          users
            .filter((user) => {
              const userName = user.name.toLowerCase();
              return (
                userName.toLowerCase().includes(search) ||
                user.login.toLowerCase().includes(search)
              );
            })
            .map((user) => {
              return <UserItem user={user} key={user.id} />;
            })
        )}
        <div className={styles.navigate}>
          <button
            className={styles.navigate_button}
            onClick={handlerBack}
            disabled={offset <= 0}
          >
            &#10094;
          </button>
          {currentPage}
          <button
            className={styles.navigate_button}
            onClick={handlerNext}
            disabled={offset >= users}
          >
            &#10095;
          </button>
        </div>
      </div>
    </>
  );
};

export default UsersList;
