import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";

import {
  getUsersRequest,
  deleteUserByIdRequest,
  setOffset,
  setCurrentPage,
} from "../../actions/usersCreators";

import { useNavigate } from "react-router-dom";
import Skeleton from "../Sceleton";
import styles from "./UserList.module.scss";
import UserItem from "../UserItem";

const UsersList = () => {
  const { users, isFetching, offset, totalUsers, currentPage } = useSelector(
    ({ users }) => users
  );
  console.log(totalUsers);
  const [search, setSearch] = useState("");

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getUsersRequest({ limit: 4, offset, currentPage }));
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [offset]);
  console.log(users);
  console.log(offset);

  const onChangeSearchValue = ({ target: { value } }) => {
    setSearch(value);
  };


  const handlerNext = () => {
    dispatch(setOffset({ offset: offset + 4 }));
    dispatch(setCurrentPage(currentPage + 1));
  };

  const handlerBack = () => {
    dispatch(setOffset({ offset: offset - 4 }));
    dispatch(setCurrentPage(currentPage - 1));
  };
  return (
    <>
      <div className={styles.card_container}>
        <label className={styles.search_inner}>
          <svg
            viewBox="0 0 20 20"
            xmlns="http://www.w3.org/2000/svg"
            style={{ width: "20px" }}
            className={styles.lupa}
          >
            <path d="M12.9 14.32a8 8 0 1 1 1.41-1.41l5.35 5.33-1.42 1.42-5.33-5.34zM8 14A6 6 0 1 0 8 2a6 6 0 0 0 0 12z" />
          </svg>
          <input
            className={styles.search_user}
            value={search}
            onChange={onChangeSearchValue}
          />
        </label>
        {isFetching ? (
          <div className={styles.skeleton}>
            <Skeleton />
            <Skeleton />
            <Skeleton />
          </div>
        ) : (
          users
            .filter((u) => {
              const userName = u.name.toLowerCase();
              return (
                userName.toLowerCase().includes(search) ||
                u.login.toLowerCase().includes(search)
              );
            })
            .map((user) => <UserItem user={user} />)
        )}
        <div className={styles.navigate}>
          <button
            className={styles.navigate_button}
            onClick={handlerBack}
            disabled={offset <= 0}
          >
            &#10094;
          </button>
          {currentPage}
          <button
            className={styles.navigate_button}
            onClick={handlerNext}
            disabled={offset > totalUsers.length - 1}
          >
            &#10095;
          </button>
        </div>
      </div>
    </>
  );
};

export default UsersList;
