import { put, call } from "redux-saga/effects";
import {
  createUserSuccess,
  createUserError,
  getUsersSuccess,
  getUsersError,
  getUserByIdSuccess,
  getUserByIdError,
  deleteUserByIdError,
  deleteUserByIdSuccess,
} from "../actions/usersCreators";
import { createUser, getAllUsers, getUserById, deleteUser } from "../api";

export function* createUserSaga(action) {
  try {
    const {
      data: { data: user },
    } = yield createUser(action.payload.values);
    yield put(createUserSuccess(user));
  } catch (error) {
    yield put(createUserError(error));
  }
}
export function* getAllUsersSaga({ payload: { page, limit, currentPage } }) {
  try {
    const {
      data: { data: users },
    } = yield call(getAllUsers, page, limit,offset);
    yield put(getUsersSuccess({ users, currentPage }));
  } catch (error) {
    yield put(getUsersError(error));
  }
}
export function* getUserByIdSaga(action) {
  try {
    const {
      data: { data: user },
    } = yield getUserById(action.payload);
    yield put(getUserByIdSuccess({ user }));
  } catch (error) {
    yield put(getUserByIdError({ error }));
  }
}

export function* deleteUserSaga(action) {
  try {
    const {
      data: { data: user },
    } = yield deleteUser(action.payload.id);
    yield put(deleteUserByIdSuccess({ user }));
  } catch (error) {
    yield put(deleteUserByIdError({ error }));
  }
}
