import { put } from "redux-saga/effects";
import { getTaskUser, createTask } from "../api";
import {
  getTaskUserSuccess,
  getTaskUserError,
  createTaskSuccess,
  createTaskError,
} from "../actions/taskCreators";

export function* getTaskUserSaga(action) {
  try {
    const {
      data: { data: tasks },
    } = yield getTaskUser(action.payload);
    yield put(getTaskUserSuccess({ tasks }));
  } catch (error) {
    yield put(getTaskUserError({ error }));
  }
}
export function* createTaskSaga(action) {
  try {
    const {
      data: { data: task },
    } = yield createTask({
      values: action.payload.data,
      userId: action.payload.userId,
    });
    yield put(createTaskSuccess({ task }));
  } catch (error) {
    yield put(createTaskError({ error }));
  }
}
