import React from "react";
import { useNavigate } from "react-router-dom";
import { useDispatch } from "react-redux";
import { deleteUserByIdRequest } from "../../actions/usersCreators";
import DeleteIcon from "@mui/icons-material/Delete";
import ModeEditIcon from "@mui/icons-material/ModeEdit";
import styles from "../UsersList/UserList.module.scss";

const stringToColor = (str) => {
  var hash = 0;
  var color = "#";
  var i;
  var value;
  var strLength;

  if (!str) {
    return color + "333333";
  }

  strLength = str.length;

  for (i = 0; i < strLength; i++) {
    hash = str.charCodeAt(i) + ((hash << 5) - hash);
  }

  for (i = 0; i < 3; i++) {
    value = (hash >> (i * 8)) & 0xff;
    color += ("00" + value.toString(16)).substring(-2);
  }

  return color;
};

const UserItem = (props) => {
  const { user } = props;
  const navigate = useNavigate();
  const dispatch = useDispatch();
  return (
    <article key={user.id} className={styles.card}>
      {user.avatar ? (
        <img
          src={`http://localhost:3001/images/${user.avatar}`}
          className={styles.avatar}
          alt={user.id}
        />
      ) : (
        <div className={styles.avatar}>
          <div className={styles.user_info_avatar}>
            <div className={styles.user_info_name} style={{bac}}></div>
          </div>
        </div>
      )}

      <div className={styles.info}>
        <p className={styles.fullname}>{user.name}</p>

        <p
          className={styles.name}
          onClick={() => {
            navigate(`/users/${user.id}`);
          }}
        >
          {user.login}
        </p>
      </div>
      <button
        className={styles.delete}
        style={{ transform: "translateX(30px)" }}
      >
        <ModeEditIcon />
      </button>
      <button
        className={styles.delete}
        onClick={() => dispatch(deleteUserByIdRequest(user.id), [])}
      >
        <DeleteIcon />
      </button>
    </article>
  );
};

export default UserItem;
