import React, { useEffect } from "react";
import { Formik, Form, Field } from "formik";
import styles from "../../UserProfile/UserProfile.module.scss";

import { updateUserTaskRequest } from "../../../actions/taskCreators";
import { useDispatch } from "react-redux";

const TaskEditFormWrapper = ({ task, formikProps }) => {
  return (
    <Form className={styles.task_edit_form}>
      <Field
        name="content"
        className={styles.content}
        component="textarea"
        placeholder="change your task..."
      />

      <input type="submit" value="save" className={styles.add_task_button} />
    </Form>
  );
};

const TaskEditForm = ({ userId, task, setModalTask }) => {
  const dispatch = useDispatch();
  const onSubmit = (values, formikBag) => {
    dispatch(updateUserTaskRequest({ values, userId, taskId: task.id }));
    setModalTask(false);
  };
  const initialValues = {
    content: task.content || "",
  };
  return (
    <Formik
      initialValues={{
        content: task.content,
      }}
      onSubmit={onSubmit}
    >
      {(formikProps) => (
        <TaskEditFormWrapper task={task} formikProps={formikProps} />
      )}
    </Formik>
  );
};

export default TaskEditForm;
