import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { useParams } from "react-router-dom";
import { getUserByIdRequest } from "../../actions/usersCreators";
import UserFormEdit from "../forms/UserFormEdit";
import styles from "./UserProfile.module.scss";

const UserProfile = () => {
  const { selectedUser } = useSelector(({ users }) => users);
  const [modalActive, setModalActive] = useState(false);

  const { userId } = useParams();
  console.log(selectedUser);
  console.log(userId);
  const dispatch = useDispatch();
  console.log(getUserByIdRequest({ userId }));

  useEffect(() => {
    dispatch(getUserByIdRequest({ userId }));
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);
  return (
    <div className={styles.wrapper_profile}>
      {selectedUser && (
        <article className={styles.profile_card}>
          <p>{selectedUser.name}</p>
          <p>{selectedUser.login}</p>
          <img
            src={`http://localhost:3001/images/${selectedUser.avatar}`}
            alt={selectedUser.name}
          />
          {modalActive && <UserFormEdit setModalActive={setModalActive} />}
        </article>
      )}
    </div>
  );
};

export default UserProfile;
