import React, { useRef } from "react";
import { Formik, Form, Field, ErrorMessage } from "formik";
import { useDispatch, useSelector } from "react-redux";
import { createUserRequest } from "../../../actions/usersCreators";
import AutoAwesomeMotionIcon from "@mui/icons-material/AutoAwesomeMotion";
import * as Yup from "yup";
import cx from "classnames";
import styles from "../UserForm.module.scss";

const UserFormEdit = () => {
  const dispatch = useDispatch();

  const filePicker = useRef(null);

  const onSubmit = (values, formikBag) => {
    dispatch(createUserRequest(values));
    formikBag.resetForm();
  };

  const handlerPick = () => filePicker.current.click();

  const validationShemas = Yup.object({
    login: Yup.string().required("Must be required"),
    password: Yup.string().required("Must be required"),
    name: Yup.string().required("Must be required"),
  });

  return (
    <>
      <Formik
        initialValues={{
          login: "",
          name: "",
          password: "",
          avatar: "",
        }}
        validationSchema={validationShemas}
        onSubmit={onSubmit}
      >
        {(formikProps) => (
          <Form className={styles.form}>
            <label className={styles.login}>
              <Field name="name">
                {({ field, form, meta }) => {
                  const inputClasses = cx(styles.registr_input, {
                    [styles.invalid]: meta.error && meta.touched,
                  });
                  return (
                    <input
                      {...field}
                      className={inputClasses}
                      placeholder="name"
                    />
                  );
                }}
              </Field>
              <ErrorMessage
                name="name"
                component="div"
                className={styles.error}
              />
            </label>
            <label className={styles.login}>
              <Field name="login">
                {({ field, form, meta }) => {
                  const inputClasses = cx(styles.registr_input, {
                    [styles.invalid]: meta.error && meta.touched,
                  });
                  return (
                    <input
                      {...field}
                      className={inputClasses}
                      placeholder="login"
                    />
                  );
                }}
              </Field>
              <ErrorMessage
                name="login"
                component="div"
                className={styles.error}
              />
            </label>
            <label className={styles.password}>
              <Field name="password">
                {({ field, form, meta }) => {
                  const inputClasses = cx(styles.registr_input, {
                    [styles.invalid]: meta.error && meta.touched,
                  });
                  return (
                    <input
                      {...field}
                      className={inputClasses}
                      placeholder="password"
                      type="password"
                    />
                  );
                }}
              </Field>
              <ErrorMessage
                name="password"
                component="div"
                className={styles.error}
              />
            </label>

            <button
              onClick={handlerPick}
              className={styles.file_picker}
              type="button"
            >
              <AutoAwesomeMotionIcon />
            </button>
            <input
              type="file"
              name="avatar"
              ref={filePicker}
              className={styles.hidden}
              onChange={(e) =>
                formikProps.setFieldValue("avatar", e.target.files[0])
              }
            />
            <input type="submit" className={styles.submit} value="Next" />
          </Form>
        )}
      </Formik>
    </>
  );
};

export default UserFormEdit;
