import React from "react";
import styles from "../UserProfile.module.scss";
import { useDispatch } from "react-redux";
const UserTaskList = ({ selectedTask }) => {

  return (
    <ul>
      {selectedTask.map((task) => (
        <li key={task.id} className={styles.task_list_item}>
          <p className={styles.content_text}>
            {task.content}
            <button
              className={styles.delete_task}
              onClick={() => {
                dispatch(deleteUserTaskRequest({ userId, taskId: task.id }));
              }}
            >
              <DeleteIcon />
            </button>
            {isEditTask ? (
              <button
                className={styles.delete_task}
                onClick={() => {
                  setIsEditTask(false);
                  setIsCreateTask(true);
                  console.log(task.id);
                }}
              >
                <ModeEditIcon />
              </button>
            ) : (
              <button
                className={styles.delete_task}
                onClick={() => {
                  setIsEditTask(true);
                  console.log(task.id);
                }}
              >
                <HighlightOffIcon />
              </button>
            )}
          </p>
          <p className={styles.date}>{task.deadline}</p>
        </li>
      ))}
    </ul>
  );
};

export default UserTaskList;
