import React from "react";
import { useDispatch } from "react-redux";
import {
  deleteUserTaskRequest,
  getTaskUserRequest,
} from "../../../actions/taskCreators";
import DeleteIcon from "@mui/icons-material/Delete";
import HighlightOffIcon from "@mui/icons-material/HighlightOff";
import ModeEditIcon from "@mui/icons-material/ModeEdit";
import { format } from "date-fns";

import styles from "../UserProfile.module.scss";

const UserTaskList = ({
  selectedTask,
  userId,
  isEditTask,
  setIsEditTask,
  setIsCreateTask,
}) => {
  const dispatch = useDispatch();
  return (
    <ul>
      {selectedTask.map((task) => (
        <li key={task.id} className={styles.task_list_item}>
          <p className={styles.content_text}>
            {task.content}
            <button
              className={styles.delete_task}
              onClick={() => {
                dispatch(deleteUserTaskRequest({ userId, taskId: task.id }));
                dispatch(getTaskUserRequest({ userId }));
              }}
            >
              <DeleteIcon />
            </button>

              <button
                className={styles.delete_task}
                onClick={() => {
                  setIsEditTask(false);
                  setIsCreateTask(true);
                  console.log(task.id);
                }}
              >
                <ModeEditIcon />
              </button>

          </p>
          <p className={styles.date}>
            {format(new Date(`${task.deadline}`), "PPPP")}
          </p>
        </li>
      ))}
    </ul>
  );
};

export default UserTaskList;
