import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { useParams } from "react-router-dom";
import { getUserByIdRequest } from "../../actions/usersCreators";
import ModeEditIcon from "@mui/icons-material/ModeEdit";
import UserFormEdit from "../forms/UserFormEdit";
import styles from "./UserProfile.module.scss";

const UserProfile = () => {
  const { selectedUser } = useSelector(({ users }) => users);
  const [modalActive, setModalActive] = useState(false);

  const { userId } = useParams();
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getUserByIdRequest({ userId }));
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);
  return (
    <div className={styles.wrapper_profile}>
      {selectedUser && (
        <article className={styles.profile_card}>
          <img
            src={`http://localhost:3001/images/${selectedUser.avatar}`}
            alt={selectedUser.name}
            className={styles.avatar}
          />

          <div className={styles.info}>
            <p className={styles.fullname}>{selectedUser.name}</p>
            <p className={styles.login}>{selectedUser.login}</p>
          </div>
          <button
            className={styles.delete}
            style={{ transform: "translateX(30px)" }}
            onClick={() => setModalActive(true)}
          >
            <ModeEditIcon />
          </button>
          {modalActive && (
            <UserFormEdit setModalActive={setModalActive} user={selectedUser} />
          )}
        </article>
      )}
      <article>

      </article>
    </div>
  );
};

export default UserProfile;
