import React, { useRef } from "react";
import { Formik, Form, Field } from "formik";
import { useDispatch, useSelector } from "react-redux";
import { createUserRequest } from "../../actions/usersCreators";
import styles from "./UserForm.module.scss";

const UserForm = () => {
  const dispatch = useDispatch();
  const { users, isFetching } = useSelector(({ users }) => users);
  console.log(isFetching);
  const filePicker = useRef(null);
  const onSubmit = (values, formikBag) => {
    dispatch(createUserRequest(values));
    console.log(values);
    formikBag.resetForm();
  };
  const handlerPick = () => filePicker.current.click();
  return (
    <>
      {isFetching ? (
        <Formik
          initialValues={{
            login: "",
            password: "",
            avatar: "",
          }}
          onSubmit={onSubmit}
        >
          {(formikProps) => (
            <Form className={styles.form}>
              <Field
                name="login"
                placeholder="login"
                className={styles.registr_input}
              />
              <Field
                name="password"
                placeholder="password"
                className={styles.registr_input}
              />
              <button onClick={handlerPick} className={styles.file_picker}>
                Chooze file..
              </button>
              <input
                type="file"
                name="avatar"
                ref={filePicker}
                className={styles.hidden}
                onChange={(e) =>
                  formikProps.setFieldValue("avatar", e.target.files[0])
                }
              />
              <input type="submit" className={styles.submit} value="Next" />
            </Form>
          )}
        </Formik>
      ) : (
        <div className={styles.finish_registr}>
          <h2>Registration Success</h2>
        </div>
      )}
    </>
  );
};

export default UserForm;
